mapper.update:
update product
set view_cnt = view_cnt + 1
where prd_no = #{prdNo}

UPDATE product
SET prd_img = #{prd_img}
WHERE prd_no = #{prd_no}

mapper.select:
SELECT prd_no, prd_name, prd_tag, price, prd_img, TO_CHAR(PRD_DATE,'YYYY--MM-DD') AS prdDate, category FROM product
AND CATEGORY = #{category}
AND prd_name LIKE '%' || #{keyword} || '%'
AND prd_tag = #{keyword}
AND (prd_name LIKE '%' || #{keyword} || '%' OR prd_tag = #{keyword})
ORDER BY prd_no DESC OFFSET #{offset} ROWS FETCH NEXT #{amount} ROWS ONLY

SELECT COUNT(*) FROM product
AND CATEGORY = #{category}
AND prd_name LIKE CONCAT('%', #{keyword}, '%')
AND prd_tag = #{keyword}
AND (prd_name LIKE CONCAT('%', #{keyword}, '%') OR prd_tag = #{keyword})
